INSERT INTO tochka(code,name,stupin)
VALUES
("ПРИХ_1",	"Закінчив 11 років життя, подав заяву прихильника юнацтва Пласту з відповідними дозволами батька/матері (опікуна/опікунки) та доданою копією свідоцтва про народження."	,0),
("ПРИХ_2",	"Розуміє та вміє розмовляти українською мовою. Пояснить важливість плекання української мови пластунами. Ходить до української школи або школи українознавства чи на курси українознавства."	,0),
("ПРИХ_3",	"Має пластовий однострій відповідних кольорів (пластову сорочку, шорти/спідницю, штуци, берет, пояс)."	,0),
("ПРИХ_4",	"Має пластовий посібник УПЮ та користується електронними посібниками для юнацтва, наприклад www.proba.plast.te.ua"	,0),
("ПРИХ_5",	"Знає, які є друковані чи електронні видання для УПЮ. Розповість в гуртку корисну інформацію, яку там почерпнула."	,0),
("ПРИХ_6",	"Знає і дотримується Трьох Головних Обов’язків Пластуна."	,0),
("ПРИХ_7",	"Пояснить свою віру в Бога. Вміє заспівати хоча б дві важливі молитви своєї конфесії. Вміє заспівати традиційні пластові молитви «Отче наш» та Царю"	,0),
("ПРИХ_8",	"Знає, що таке Пласт, мету організації та її коротку історію."	,0),
("ПРИХ_9",	"Вміє по-пластовому привітатися."	,0),
("ПРИХ_10",	"Візьме участь у щонайменше 6 сходинах свого пластового гуртка та в одній одноденній прогулянці гуртка чи куреня."	,0),
("ПРИХ_11",	"Знає, як виглядає пластова відзнака і що вона символізує"	,0),
("ПРИХ_12",	"Знає, хто є патроном Пласту і свого куреня."	,0),
("ПРИХ_13",	"Знає всіх членів свого гуртка, членів курінного проводу, свого впорядника та зв’язкового. Вміє з ними швидко сконтактувати."	,0),
("ПРИХ_14",	"Платить членську вкладку із власного заробітку."	,0);
("1_А_1",	"Знає на пам’ять і пояснить Три Головні Обов’язки Пластуна.",	1),
("1_А_2",	"Відбуває релігійну практику в своєму обряді.",	1),
("1_А_3",	"Знає на пам’ять і пояснить 14 точок Пластового Закону.",	1),
("1_А_4",	"Знає і заспіває український державний гімн, намалює герб України і розкаже про його походження. Знає, як виглядає та що символізує український прапор, вміє його шанувати.",	1),
("1_А_5",	"Щоденно спілкується українською мовою та дбає про чистоту свого мовлення. Налаштує українську мову в своїх електронних пристроях та програмах, якими користується.",	1),
("1_А_6",	"Пояснить пластове розуміння доброго діла та як практикує його в щоденному житті. Зробить добре діло з гуртком.",	1),
("1_А_7",	"Знає головні географічні характеристики України (площа, населення, області, інше), головні події з новітньої історії України (Перші та другі визвольні змагання, дисидентський рух, відновлення незалежності, Майдани) та сучасного провідника Української держави.",	1),
("1_А_8",	"Знає історію і сучасність свого міста чи села.",	1),
("1_А_9",	"Знає про поселення українців поза межами України. Виконає проект «Пласт та українці у світі» (наприклад, доповнить чи виправить відповідну статтю у Вікіпедії).",	1),
("1_А_10",	"Добре навчається в школі та підтвердить це відповідним документом.",	1),
("1_А_11",	"На сходинах гуртка розповість про останню прочитану книгу позашкільної програми.",	1),
("1_Б_1",	"Знає хто, коли і для чого заснував скаутський рух (Бейден Поуел) та Пласт (Іван Чмола, Петро Франко, Олександр Тисовський).",	1),
("1_Б_2",	"Заспіває і пояснить Пластовий Обіт. Знає його авторів.",	1),
("1_Б_3",	"Пояснить пластове гасло СКОБ та його символи.",	1),
("1_Б_4",	"Знає та заспіває Пластовий Гімн та Гімн Закарпатських Пластунів, знає їх авторів.",	1),
("1_Б_5",	"Намалює і пояснить пластову відзнаку, знає де її можна і треба носити та зображати. Знайомий із основними правилами бренд-буку Пласту.",	1),
("1_Б_6",	"Знає хто і чому є патроном Пласту. Знає життєпис патрона Пласту.",	1),
("1_Б_7",	"Знає життєпис патрона свого куреня та прикмети характеру, що зробили його гідним наслідування.",	1),
("1_В_1",	"Знає та пояснить організацію гуртка, права та обов’язки членів гуртка, гурткових діловодів та їх відзнаки. Успішно виконує своє гурткове діловодство.",	1),
("1_В_2",	"Знає організацію куреня та пояснить обов’язки діловодів. Знає відзнаки курінних діловодів, хто є в проводі куреня. Може швидко із ними сконтактувати.",	1),
("1_В_3",	"Знає, хто є станичним/-ою і структуру станичного проводу. Знає, які відзнаки носять ці діловоди.",	1),
("1_В_4",	"Знає, які відзначення і перестороги надаються в Пласті. Знає відзнаки пластових відзначень.",	1),
("1_Г_1",	"Знає і заспіває не менше, ніж 4 народні та 4 пластові пісні, пояснить їх походження.",	1),
("1_Г_2",	"Вміє надавати й виконувати впорядові накази в гуртку.",	1),
("1_Г_3",	"Знає правила чесної гри і їх дотримується. Пояснить і проведе 3 гри з гуртком чи куренем. Пояснить їх призначення та користь.",	1),
("1_Г_4",	"Читає пластові публікації та зробить допис на пластову тематику.",	1),
("1_Г_5",	"Візьме участь у 3-х пластових заходах (спільному занятті з іншим гуртком, тереновій грі, спортивному змазі, інтелектуальному, мистецькому, майстерці чи іншому заході).",	1),
("1_Г_6",	"Знає, які є види прогулянок (екскурсій) і візьме участь в одній з них зі своїм гуртком.",	1),
("1_Ґ_1",	"Знає, чому треба дбати про довкілля і пояснить, як це сама робить.",	1),
("1_Ґ_2",	"Знає по 10 рослин і 10 тварин своєї околиці, небезпечні і корисні рослини і тварини, розпізнає сліди не менше 6 тварин.",	1),
("1_Ґ_3",	"Вміє збудувати гніздо для розпалення вогню; запалить його не більше, як трьома сірниками; правильно загасить і затре слід від вогню.",	1),
("1_Ґ_4",	"Знає, який особистий виряд потрібен на одноденну прогульку і спакує наплечник.",	1),
("1_Ґ_5",	"Знає, який особистий виряд потрібен на табір і спакує наплечник.",	1),
("1_Ґ_6",	"Зав’яже 6 вузлів та одне в’язання, знає, коли їх застосовувати. Візьме участь у побудові піонірської споруди. Знає правила відповідальної піонірки.",	1),
("1_Ґ_7",	"Знає, як правильно користуватись, зберігати та транспортувати ніж, сокиру, лопату, пилу.",	1),
("1_Ґ_8",	"Вміє вибрати місце під шатро, розставити його та спакувати, затерти сліди свого перебування, згідно з правилами екологічного таборування.",	1),
("1_Ґ_9",	"Відбуде одну гурткову чи курінну мандрівку в природі з нічлігом.",	1),
("1_Ґ_10",	"Відбуде один курінний або окружний виховний табір за програмою першої проби.",	1),
("1_Д_1",	"Вміє викликати лікаря, міліцію, пожежну охорону (рятувальників), газову службу. Може швидко дізнатись графік руху потрібного транспорту.",	1),
("1_Д_2",	"Вміє надати допомогу у випадках: поранення, сонячного удару, опіку чи переохолодження/обмороження.",	1),
("1_Д_3",	"Покаже сторони світу за допомогою компаса, сонця та зір. Вміє зорієнтувати карту на місцевості.",	1),
("1_Д_4",	"Знає основні знаки на топографічній карті. Зайде у визначене на цій карті місце. Вміє користуватися сучасними способами навігації у місті та електронними картами.",	1),
("1_Д_5",	"Вміє правильно користуватися різними засобами обміну інформацією: пошта (звичайна та електронна), Інтернет, телефон.",	1),
("1_Д_6",	"Знає правила доброї поведінки та дотримується їх в житті. Візьме участь у гутірці про добру поведінку.",	1),
("1_Д_7",	"Знає правила дорожнього руху для пішохода та велосипедиста.",	1),
("1_Д_8",	"Володіє іноземною мовою на доступному рівні (щонайменше А2 за глобальною шкалою), щоб пояснити особисті потреби, відрекомендуватися та відповісти на запитання про себе чи інших людей. Візьме участь у діалозі на довільну тему іноземною мовою.",	1),
("1_Д_9",	"Знає правила ведення дискусії. Візьме участь у дискусії на довільну тему.",	1),
("1_Е_1",	"Знає основні вправи щоденної руханки та практикує її щоденя. Регулярно, принаймні двічі на тиждень, відвідує спортивну секцію або займається рухливими іграми/спортом.",	1),
("1_Е_2",	"Вміє плавати та пропливе будь-яким стилем 50 метрів.",	1),
("1_Е_3",	"Здобуде ВФВ свого віку (за бажанням).",	1),
("1_Є_1",	"Знає, де знайти перелік пластових вмілостей, як їх здавати та де пришивати здобуті.",	1),
("1_Є_2",	"Здобуде пластову вмілість «Перша Медична допомога І».",	1),
("1_Є_3",	"Здобуде 5 пластових вмілостей Першої Проби (серед яких рекомендовано «Домашню зарадність І»).",	1),
("2_А_1",	"Візьме участь у дискусії про Три Головні Обов’язки Пластуна, де подасть приклади застосування їх в особистому житті.",	2),
("2_А_2",	"Знає церковний обряд, традиції та головні свята свого віровизнання. Знає основні засади інших світових релігій.",	2),
("2_А_3",	"Візьме участь у дискусії про Пластовий закон, де подасть приклади застосування його в особистому житті.",	2),
("2_А_4",	"Знає історію української національної символіки.",	2),
("2_А_5",	"Доведе, що читає україномовні (в т.ч. пластові) книжки, журнали і газети, електронні видання регіонального та національного рівня.",	2),
("2_А_6",	"Підготує і виконає проект для плекання українських національних релігійних традицій, залучаючи до реалізації свій гурток або курінь (День святого Миколая, Андрія, Маланка, Вертеп, Гаївки, Свято Весни, Купала тощо).",	2),
("2_А_7",	"Підготує і виконає соціальний (суспільний) проект пластового доброго діла, залучаючи до реалізації свій гурток або курінь.",	2),
("2_А_8",	"Підготує і виконає проект громадянського (національно-патріотичного) виховання, залучаючи до реалізації свій гурток або курінь (Повстанська Ватра на Покрову, Листопадовий чин, акція «Пам’ятай про Крути» тощо).",	2),
("2_А_9",	"Знає права та обов’язки громадянина України згідно Конституції.",	2),
("2_А_10",	"Підтвердить успішність свого навчання в школі (гімназії, ліцеї, коледжі) відповідним документом.",	2),
("2_Б_1",	"Візьме участь у дискусії, в якій пояснить чому належить до Пласту.",	2),
("2_Б_2",	"Знає в загальному історію Пласту в його шести етапах, а саме: 1911-1920; 1920-1930; 1930-1945; 1945-1950; 1950-1990; 1990-по сьогодні.",	2),
("2_Б_3",	"Веде успішно діловодство гурткового або курінне діловодство або допомагає підбратчиком/сестричкою в новацтві щонайменше 6 місяців.",	2),
("2_Б_4",	"Знає, з яких частин складається Крайова Органіазція і світовий Пласт, назве головних членів Станичної, Крайової, Головної старшини. Вміє розрізняти їх відзнаки.",	2),
("2_Б_5",	"Вміє надавати та виконувати впорядові накази в курені.",	2),
("2_Б_6",	"Візьме участь в організації або організує теренову гру.",	2),
("2_Б_7",	"Пояснить, як підготувати і провести гутірку і що потрібно, щоб її успішно провести. Підготує гутірку з використанням електронної презентації.",	2),
("2_Б_8",	"Підготує і проведе з гуртком серію сходин з гутірками, іграми, піснями, майстерками, впорядом та іншими елементами пластових сходин.",	2),
("2_Б_9",	"Організує гурток або команду від куреня на станичний/окружний або крайовий захід: спортивний чи мистецький змаг, теренову гру, табір тощо.",	2),
("2_Б_10",	"Організує з гуртком або куренем одну прогулянку (екскурсію).",	2),
("2_В_1",	"Знає і заспіває щонайменше 5 пластових, 5 стрілецьких і повстанських, 5 обрядових і 5 сучасних українських пісень. Знає їх походження.",	2),
("2_В_2",	"Знає екологічну ситуацію свого регіону та екологічні проблеми України.",	2),
("2_В_3",	"Знає основні природоохоронні об’єкти своєї держави та своєї області, найбільш поширені види рослин і тварин України, небезпеки від них. Знайде і опише або зафіксує хоча б 10 слідів тварин.",	2),
("2_Г_1",	"Візьме участь у змаганні з лисячого бігу (спортивному орієнтуванні).",	2),
("2_Г_2",	"Знає, які є роди ватер, вміє збудувати ватру, запалити не більше, як двома сірниками, загасити і затерти сліди від вогню.",	2),
("2_Г_3",	"Зварить на польовій кухні обід з двох страв.",	2),
("2_Г_4",	"Зав’яже 10 вузлів і 2 в’язання, знає, як і коли їх застосовувати. Організує з гуртком/куренем будівництво таборової споруди.",	2),
("2_Г_5",	"Збудує в природі кухню, знає що робити з відпадками. Знає, які споруди мають бути на таборовій кухні та де вони повинні знаходитися.",	2),
("2_Г_6",	"Організує з гуртком дві гурткові або курінні мандрівки з нічлігом.",	2),
("2_Г_7",	"Відбуде успішно виховний окружний/крайовий табір.",	2),
("2_Ґ_1",	"Успішно пройде курс долікарської медичної допомоги (пластовий або інший - наприклад, Мальтійської служби допомоги) та представить відповідний документ.",	2),
("2_Ґ_2",	"Орієнтується в своїй області, знає головні об’єкти і шляхи сполучення, знайде на місцевості місце, вказане на карті.",	2),
("2_Ґ_3",	"Вміє правильно користуватись інструментами підвищення ефективності (офіс, онлайн календар). Вміє планувати свій час за допомогою електронних календарів.",	2),
("2_Ґ_4",	"Складе кошторис та фінансовий звіт одної із запропонованих акцій (курінна мандрівка, табір).",	2),
("2_Ґ_5",	"Володіє іноземною мовою на доступному рівні (щонайменше B1 за глобальною шкалою), і для цього виконає наступні вимоги (одну на вибір): А. Представить власний життєпис на гурткових сходинах; Б. Організує гутірку дискусію) на обрану тему; В. Візьме участь у тренінгу (школі) іноземною мовою; Г. Розповість про прочитану книгу іноземною мовою.",	2),
("2_Д_1",	"Дбає про своє здоров’я. Проведе руханку з гуртком або куренем.",	2),
